mapper.insert:
INSERT INTO USER (
	EMAIL
	, NAME
	, PASSWORD
	, ROLE
	, FRST_REG_DATE
	, FRST_REG_USER_ID
	, LAST_CHG_DATE
	, LAST_CHG_USER_ID
) VALUES (
	#{email}
	, #{name}
	, #{password}
	, #{role}

		, #{frstRegDate}
		, current_timestamp()


		, #{frstRegUserId}
		, 'SYSTEM'


		, #{lastChgDate}
		, current_timestamp()


		, #{lastChgUserId}
		, 'SYSTEM'

)

mapper.update:
UPDATE USER
	SET 
		EMAIL = #{email} 
		, NAME = #{name} 
		, PASSWORD = #{password} 
		, ROLE = #{role}

			, LAST_CHG_DATE = #{lastChgDate}
			, LAST_CHG_DATE = current_timestamp()

		, LAST_CHG_USER_ID = #{lastChgUserId}
WHERE ID = #{id}
